mapper.select:
select * from order_entity
where lottery_id = #{lotteryId}
and round = #{round}
and status = #{status}

mapper.update:
update from order_entity
where id = #{id}
set fee = #{result.fee}
and bet_prize = #{result.betPrize}
and status = #{result.status}
and game_status = #{result.gameStatus}
and updated_time = NOW()

